import React, { Component} from 'react'
import friendly1 from '../Assets/Friendly1.png'
import friendlySad from '../Assets/FriendlySad.png'
import Satellite from '../Assets/Satellite.png'
import Explosion from '../Assets/Explosion.png'




const Targets = (props) => {

        if(this.props.target_type === 'debris'){
            return (
                <div className='tile-target' onClick={() => this.props.handleClick(this.props.name, this.props.target_type)} style={{backgroundColor: this.props.isClicked ? '#1D9067' : this.props.debrisBackgroundColor}} >
                    <img className='tile-img' src={this.props.isClicked ? Explosion : Satellite}/>
                </div>
            )
        } 
        if(this.props.target_type === 'friendly'){
            return(
                <div className='tile-target' onClick={() => this.props.handleClick(this.props.name, this.props.target_type)} style={{backgroundColor: this.props.isClicked ? '#3F1923' : this.props.friendlyBackgroundColor}}>
                    <img className='tile-img' src={this.props.isClicked ? friendlySad : friendly1}/>
            </div>
            )
        }
}
