import React, { Component } from 'react';
import Tiles from './Tiles'


export default class HomeScreen extends Component{

    state = {
        play: false
    }

    levelSelect = () => {
        return(
            <div>
                <h1>Hey There from HomeScreen</h1>
                <button onClick={() => this.loadLevel()}>Start</button> {/* this will take info later to determine which level */}
            </div>
        )
    }
    loadLevel = () => {
        this.setState({
            play: true
        })
    }
    play = () => {
        return (
            <div className='fl'>
                <h1>This is where the level will be loaded and played!</h1>
                <Tiles/>
            </div>
        )
    }



    render() {
        if (this.state.play === false){
            return (
                this.levelSelect()
            )
        } else {
            return (
                this.play()
            )
        }
    }



}
