import React, { Component} from 'react'
import '../App.css'
import friendly1 from '../Assets/Friendly1.png'
import friendlySad from '../Assets/FriendlySad.png'



export default class Tiles extends Component{

    state = {
        backgroundColor: '#18FCFF',
        isClicked: false,
        count: 0
    }

    resetState = () => {
            this.setState({
            isClicked: false,
            backgroundColor: '#18FCFF'
        })
    }


    handleClick = () => {
        if(this.props.target_type === "friendly"){
            this.setState({
                isClicked: true
            })
        } else if(this.props.target_type === "debris"){
            this.isClicked = true
        }
    }

    componentDidMount = () => {
        console.log(this.props)
        // this.resetState()
    }

    componentDidUpdate = () => {
        // if(this.state.isClicked){ 
        //     // this.setState({
        //     //     isClicked: false,
        //     // })
        //     console.log(this.state.isClicked)
        // }
    }

    render() {
        if(this.props.target_type === 'debris'){ 
            // if(this.state.isClicked){
            //     this.resetState()
            // }
            // console.log(this.state.isClicked)
            return (
                <div className='tile-target' onClick={() => this.handleClick(this.props.name)} style={{backgroundColor: this.isClicked ? 'green' : this.state.backgroundColor}} >
                    <p>{this.props.target_type}</p>
                </div>
            )
        } 
        if(this.props.target_type === 'friendly'){       
            return(
                <div className='tile-target' onClick={() => this.handleClick} style={{backgroundColor: this.isClicked ? '#3F1923' : this.state.backgroundColor}}>
                    <img className='tile-img' src={this.isClicked ? friendlySad : friendly1}/>
            </div>
            )
        }

    }
}
