import React, { Component } from 'react'
import Logo from '../Assets/Logo.png'
import HomeScreen from './HomeScreen'
import FlipMove from 'react-flip-move'


export default class TitleScreen extends Component {

    state = { 
        titleScreen: true,
        story: false
    }

    loadHomeScreen = () => {
        console.log('hello')
        this.setState({
            story: true,
            titleScreen: false
        })
    }

    render(){
        if(this.state.titleScreen === true){
            return(
                <div className='title-screen-background'>
                    <FlipMove
                    duration={1500}
                    appearAnimation="fade"
                    enterAnimation="fade"
                    leaveAnimation="fade"
                    >
                    <div className='logo-container' style={{display: this.state.story ? 'none' : 'inline-block'}}>
                            <button onClick={() => this.loadHomeScreen()}><img src={Logo}/></button>
                    </div>
                    </FlipMove>
                </div>
            )
        }
        else if(this.state.story === true){
            return(
                <div className='title-screen-background'>
                        <div className='story-container'>
                            <p className='text'>Hey, Here's where the intro story goes</p>
                        </div>

                </div>
            )

        }
        // else{
        //     return(
        //         <HomeScreen/>
        //     )
        // }

    }
}
// logo-container  level select container
