import React, { Component } from 'react'
import Logo from '../Assets/Logo.png'
import HomeScreen from './HomeScreen'

const story = ["Welcome To React Blaster", "As A Member Of NASA's Space Cleanup Crew, It Is Your Job To Destroy The Debris Orbiting The Earth."
    ]


export default class TitleScreen extends Component {

    state = { 
        titleScreen: true,
        intro: false,
        storyValue: 0
    }

    loadStory = () => {
        this.setState({
            intro: true,
            titleScreen: false
        })
    }
    runIntro = () => {

        var introLoop = setInterval(() => {
            this.getNext();
            if(story[this.state.storyValue] === null){
                clearInterval(introLoop)
            }
        }, 2000)

    }

    getNext = () => {
        this.setState({
            storyValue: this.state.storyValue+1
        })
    }

    getStart = () => {
        return(
            <button className='hvr-shadow-radial' onClick={() => this.loadHomeScreen()} >Start</button>
        )
    }

    loadHomeScreen = () => {
        console.log('hello')
        this.setState({
            story: null,
            start: false,
            titleScreen: false
        })
    }



    render(){
        if(this.state.titleScreen === true){
            return(
                <div className='title-screen-background'>
                    <div className='logo-container' style={{display: this.state.story ? 'none' : 'inline-block'}}>
                        <img src={Logo} onClick={() => this.loadStory()}/>
                    </div>
                </div>
            )
        }
        else if(this.state.intro === true){
            return(
                <div className='title-screen-background'>
                    <div className='story-container'>
                        <p className='story-text'>{story[this.state.storyValue]}</p>
                        {this.runIntro()}

                        </div>
                </div>
            )

        }
        else if(this.state.titleScreen === false && this.state.story === false){
            return(
                <HomeScreen/>
            )
        }

    }
}
// logo-container  level select container
