import React, { Component } from 'react';
import LevelContainer from '../Containers/LevelContainer'
import LevelSelector from '../Components/LevelSelector'


export default class HomeScreen extends Component{

    state = {
        play: false,
        levels: [],
        selectedLevel: {
            name: null,
            background_url: null,
            difficulty: null,
            song_url: null
        }
    }

    componentDidMount = () => {
        this.getLevels()
    }

    levelSelect = () => {
        return(
            <div>
                <div className='home-screen-header'>
                    <h4>Select A Level</h4>
                </div>

                <div className='home-screen-body'>
                    {this.state.levels.map((level, i) => <LevelSelector key={i}{...level} 
                    loadLevel={this.loadLevel}
                    />)}
                </div>
            </div>
        )
    }
    loadLevel = (info) => {

        console.log(info)
        this.setState({
            selectedLevel: {...infop}
        })
        // console.log(this.state.selectedLevel)
        // this.play()

    }

    getLevels = () => {
        fetch('http://localhost:3000/levels')
        .then(resp => resp.json())
        .then(data => {
           this.setState({
               levels: data
           })
        })
    }

    play = (obj) => {
        return (
                <LevelContainer thisLevel={this.state.selectedLevel}/>
        )
    }



    render() {
        console.log('home render '+this.state.selectedLevel)
        if (this.state.play === false){
            return (
                this.levelSelect()
            )
        } else {
            return (
                this.play()
            )
        }
    }



}
