    state = {
        isClicked: false,
        backgroundColor: '#18FCFF',
        count: 0
    }


    resetState = () => {
            this.setState({
            isClicked: false,
            backgroundColor: '#18FCFF'
        })
    }


    handleClick = () => {
        if(this.props.target_type === "friendly"){
            this.setState({
                isClicked: true,
            })
        } else if(this.props.target_type === "debris"){
            this.setState({
                isClicked: true,
            })
        }
    }
