        if(this.state.titleScreen === true){
            return(
                <div className='title-screen-background'>
                    <div className='logo-container' style={{display: this.state.story ? 'none' : 'inline-block'}}>
                        <img src={Logo} onClick={() => this.loadHomeScreen()}/>
                    </div>
                </div>
            )
        }
        else if(this.state.story === true){
            return(
                <div className='title-screen-background'>
                    <FlipMove
                    duration={1500}
                    appearAnimation="fade"
                    enterAnimation="fade"
                    leaveAnimation="fade"
                    >
                        <div className='story-container'>
                            <p className='story-text'>Welcome To React Blaster</p>
                            <p className='story-text'>As A Member Of NASA's Space Cleanup Crew, 
                            It Is Your Job To Destroy The Debris Orbiting The Earth. </p>
                            <button className='hvr-shadow-radial'>More</button>
                        </div>
                    </FlipMove>
                </div>
            )

        }
        // else{
