import React, { Component } from 'react'
import Logo from '../Assets/Logo.png'
import HomeScreen from './HomeScreen'
import FlipMove from 'react-flip-move'


export default class TitleScreen extends Component {

    state = { 
        titleScreen: true,
        intro: false,
        storyValue: 0
    }

    loadStory = () => {
        this.setState({
            intro: true,
            titleScreen: false
        })
    }

    getStory = () => {

        return(story[this.state.storyValue])
    }

    getNext = () => {
        this.setState({
            storyValue: this.state.storyValue+1
        })
    }

    loadHomeScreen = () => {
        console.log('hello')
        this.setState({
            story: null,
            start: false,
            titleScreen: false
        })
    }



    render(){
        if(this.state.titleScreen === true){
            return(
                <div className='title-screen-background'>
                    <div className='logo-container' style={{display: this.state.story ? 'none' : 'inline-block'}}>
                        <img src={Logo} onClick={() => this.loadStory()}/>
                    </div>
                </div>
            )
        }
        else if(this.state.intro === true){
            return(
                <div className='title-screen-background'>
                    <FlipMove
                    duration={1500}
                    appearAnimation="fade"
                    enterAnimation="fade"
                    leaveAnimation="fade"
                    >
                        <div className='story-container'>
                            <p className='story-text'>{this.getStory()}</p>
                            <button className='hvr-shadow-radial' onClick={() => this.getNext()} >...</button>
                        </div>
                    </FlipMove>
                </div>
            )

        }
        else if(this.state.titleScreen === false && this.state.story == false){
            return(
                <HomeScreen/>
            )
        }

    }
}
// logo-container  level select container
