import React, { Component} from 'react'
import '../App.css'
import friendly1 from '../Assets/Friendly1.png'
import friendlySad from '../Assets/FriendlySad.png'

const initialState = {
    isClicked: false,
    backgroundColor: '#18FCFF'
}

export default class Tiles extends Component{

    constructor (){


    this.state = this.initialState.isClicked

    }


    resetState = () => {
            this.setState({
            isClicked: false,
            backgroundColor: '#18FCFF'
        })
    }


    handleClick = () => {
        if(this.props.target_type === "friendly"){
            console.log(this.state.isClicked)
            this.setState({
                isClicked: true,
            })
        } else {
            this.setState({
                isClicked: true,
            })
        }

    }

    render() {

        if(this.props.target_type === 'debris'){
            // if(this.state.clicked){
            //     this.resetState()
            // }
            return (
                <div className='tile-target' onClick={this.handleClick} style={{backgroundColor: this.state.isClicked ? 'green' : this.state.backgroundColor}} >
                    <p>{this.props.target_type}</p>
                </div>
            )
        } else if(this.props.target_type === 'friendly'){
            // if(this.state.clicked){
            //     this.resetState()
            // }
            return(
                <div className='tile-target' onClick={this.handleClick} style={{backgroundColor: this.state.isClicked ? '#3F1923' : this.state.backgroundColor}}>
                    <img className='tile-img' style={{backgroundColor: this.state.friendlyBackgroundColor}} 
                    src={this.state.isClicked ? friendlySad : friendly1}/>
            </div>
            )
        }

    }
}
