import React, { Component } from 'react'
import HealthPotion from '../Assets/RepairsPotion.png'

export default class ShopItem extends Component {


    getItemImg = (name) => {
        switch(name){
            case 'Regular Health Potion':
                return HealthPotion;
            default:
                return HealthPotion;
        }
    }

    //desc:  <p className='text'>{this.props.description}{<br></br>}Price: {this.props.price} points</p>


    render(){
        return(
                <div>
                    <div className='shop-item' >
                        <button className='hvr-pulse-grow' onClick={() => this.props.handlePurchase(this.props.name)}><img src={this.getItemImg(this.props.name)}/></button>
                        <p className='text'>{this.props.description}{<br></br>}Price: {this.props.price} points</p>
                        {/* <p className='text'>Full Health. {<br></br>} Price: 50 Points</p> */}
                    </div>            
                </div>     
        )
    }
}
