import React, { Component } from 'react';
import Tiles from '../Components/Tiles'
import '../App.css'
import friendly1 from '../Assets/Friendly1.png'
import friendlySad from '../Assets/FriendlySad.png'



export default class LevelContainer extends Component{

    //refactor later to use a switch statement to run through these functions based on which level is selected

    state = {
        selectedLevel: this.props.selectedLevel,
        targets: null,
        counter: 0,
        isClicked: false
        // tiles: [{name: 'one'},{name: 'two'},{name: 'three'},{name: 'four'},{name: 'five'},{name: 'six'},{name: 'seven'},{name: 'eight'},{name: 'nine'},{name: 'ten'},{name: 'eleven'},{name: 'twelve'}]
    }

    componentDidMount = () => {
        this.interval = setInterval(() =>{
            this.loadLevelOne()
            this.setState({
                counter: this.state.counter + 1
            })
            console.log('container '+this.state.counter)
        }, 4000)    
    }


    //figure out randomization and then looping
    loadLevelOneGrid = () => {
        let targets = [{name: 'one', target_type: 'debris'},{name: 'two', target_type: 'debris'},{name: 'three', target_type: 'debris'},{name: 'four', target_type: 'friendly'}];
        let targetsCopy = [];
        targetsCopy = this.shuffleGrid(targets)
        return targetsCopy
    }

    loadLevelOne = () => {
       let targets = this.loadLevelOneGrid()
       this.setState({
           targets: targets
       })
    }

    shuffleGrid = (array) => {
        let newArr = [];
        while(newArr.length < array.length){
            let x = array[Math.floor(Math.random() * array.length)]
            if (!newArr.includes(x)){
                newArr.push(x)
            }
        }
        return newArr
    }

    render() {
        if (this.state.targets === null){
            return <h1>LOADING!</h1>
        }
        return (
            <div className='tile-grid-container'>
                <div className='tile-grid'>
                    {this.state.targets.map((tile, i) => <Tiles isClicked={this.state.isClick}key={i}{...tile}/>)}
                </div>
            </div>
        )
    }

}
