import React, { Component} from 'react'
import '../App.css'
import friendly1 from '../Assets/Friendly1.png'
import friendlySad from '../Assets/FriendlySad.png'



export default class Tiles extends Component{

    componentDidMount = () => {
        const friendlyURL = isClicked ? friendlySad : friendly1
        let isClicked = false;
    }


    handleClick = () => {
        isClicked = !isClicked
    }

    render() {
        return (
            <div className='tile-target'>
                <img className='tile-img' src={friendlyURL}/>
            </div>
        )
    }
}
