import React from 'react'
import { Spring } from 'react-spring/renderprops'
import friendly1 from '../Assets/Friendly1.png'
import friendlySad from '../Assets/FriendlySad.png'
import Satellite from '../Assets/Satellite.png'
import Explosion from '../Assets/Explosion.png'




export default function Targets(props) {



        if(props.target_type === 'debris'){

            return (
                <Spring
                from={{opacity: 0}}
                to={{opacity: 1}}
                >
                <div className='tile-target' onClick={() => props.handleClick(props.name, props.target_type)} style={{backgroundColor: props.isClicked ? '#1D9067' : props.debrisBackgroundColor}} >
                    <img className='tile-img' src={props.isClicked ? Explosion : Satellite}/>
                </div>
                </Spring>
            )
        } 
        if(props.target_type === 'friendly'){
            return(
                <div className='tile-target' onClick={() => props.handleClick(props.name, props.target_type)} style={{backgroundColor: props.isClicked ? '#3F1923' : props.friendlyBackgroundColor}}>
                    <img className='tile-img' src={props.isClicked ? friendlySad : friendly1}/>
            </div>
            )
        }

    }
