import React, { Component} from 'react'
import '../App.css'
import friendly1 from '../Assets/Friendly1.png'
import friendlySad from '../Assets/FriendlySad.png'



export default class Tiles extends Component{

    state = {
        isClicked: false
    }



    handleClick = () => {
        this.setState({
            isClicked: false
        })
    }

    render() {
        return (
            <div className='tile-target'>
                <img className='tile-img' src={this.state.isClicked ? friendly1 : friendlySad}/>
            </div>
        )
    }
}
