import React, { Component } from 'react'
import HealthPotion from '../Assets/RepairsPotion.png'

export default class ShopItem extends Component {



    // <img src={HealthPotion}/>
    render(){
        return(

                <div>
                    <div className='shop-item' >
                        <button className='hvr-pulse-grow' onClick={() => this.props.handlePurchase(this.props.name)}></button>
                        <p className='text'>{this.props.description}{<br></br>}Price: {this.props.price}</p>
                        {/* <p className='text'>Full Health. {<br></br>} Price: 50 Points</p> */}
                    </div>            
                </div>     
        )
    }
}
